create database aurora;
use aurora;
create table alumnes (
    matricula int(8) NOT NULL PRIMARY KEY,
    correu_alu varchar(35) NOT NULL,
    nom_alu varchar(20) NOT NULL,
    1rCognom_alu varchar(25) NOT NULL,
    2nCognom_alu varchar(25) NULL,
    adreça_alu varchar(45) NOT NULL,
    telefon_alu char(9) NOT NULL,
    DNI_alu char(8) NOT NULL,
    id_curs int(2) NOT NULL
);

create table professor (
    num_SS_prof bigint(12) NOT NULL PRIMARY KEY,
    nom_prof varchar(20) NOT NULL,
    1rCognom_prof varchar(25) NOT NULL,
    2nCognom_prof varchar(25) NULL,
    adreça_prof varchar(45) NOT NULL,
    telefon_prof char(9) NOT NULL,
    DNI_prof char(8) NOT NULL,
    correu_prof varchar(35) NOT NULL
);

-- Hacía falta meter Bigint debido a que int tiene rango hasta el 2,147,483,647 hasta el número

create table materia (
    id_materia int(4) NOT NULL PRIMARY KEY auto_increment,
    nom_materia varchar(80) NOT NULL,
    id_curs int(2) NOT NULL,
    num_SS_prof bigint(12) NOT NULL
);

create table curs (
    id_curs int(2) NOT NULL PRIMARY KEY auto_increment,
    horario set("Mañana","Tarde"),
    nom_curs varchar(50) NOT NULL,
    id_aula int(3) NOT NULL
);

create table aula (
    id_aula int(3) NOT NULL PRIMARY KEY,
    pis_aula int(1) NOT NULL
);

create table ensenyament (
    id_ensenyament int(2) NOT NULL PRIMARY KEY auto_increment,
    matricula int(8) NOT NULL,
    num_SS_prof bigint(12) NOT NULL
);


create table alu_materia (
    id_alumateria int(4) NOT NULL PRIMARY KEY auto_increment,
    matricula int(8) NOT NULL,
    id_materia int(4) NOT NULL
);

ALTER TABLE alumnes ADD CONSTRAINT fk_alumne_curso FOREIGN KEY (id_curs) REFERENCES curs(id_curs);
ALTER TABLE materia ADD CONSTRAINT fk_materia_curso FOREIGN KEY (id_curs) REFERENCES curs(id_curs);
ALTER TABLE materia ADD CONSTRAINT fk_materia_prof FOREIGN KEY (num_SS_prof) REFERENCES professor(num_SS_prof);
ALTER TABLE curs ADD CONSTRAINT fk_curs_aula FOREIGN KEY (id_aula) REFERENCES aula(id_aula);
ALTER TABLE ensenyament ADD CONSTRAINT fk_ensenyament_aula FOREIGN KEY (matricula) REFERENCES alumnes(matricula);
ALTER TABLE ensenyament ADD CONSTRAINT fk_ensenyament_prof FOREIGN KEY (num_SS_prof) REFERENCES professor(num_SS_prof);
ALTER TABLE alu_materia ADD CONSTRAINT fk_alu_matricula FOREIGN KEY (matricula) REFERENCES alumnes(matricula);
ALTER TABLE alu_materia ADD CONSTRAINT fk_alu_materia FOREIGN KEY (id_materia) REFERENCES materia(id_materia);





